      <div>
        <h2>{this.props.month}</h2>
        <h3>{this.TotalMonthRewards}</h3>
        <div>
        {transitionList.map((eachTransition, i) => (
          <RewardsCard
          {...eachTransition}
          key={i}
          purchased={eachTransition.purchase}
          rewardsPoints={this.TotalMonthRewards}
          />
