import React, { Component } from 'react';
import { Link} from 'react-router-dom';
import '../App.css';
import axios from 'axios';

class customerRewards extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customer_rewards: []
    };
  }

  // componentDidMount() {
  //   // console.log("Print id: " + this.props.match.params.account);
  //   axios
  //     .get('http://localhost:5000/customers/'+this.props.match.params.account)
  //     .then(res => {
  //       // console.log("Print-customerRewards-API-response: " + res.data);
  //       this.setState({
  //         customer: res.data
  //       })
  //     })
  //     .catch(err => {
  //       console.log("Error from customerRewards");
  //     })
  // };

  componentDidMount() {
    // const { accountId } = this.props.match.params;
    console.log("Print id: " + this.props.match.params.id);
    axios
      .get('http://localhost:5000/customers/'+this.props.match.params.id)
      .then(res => {
        // console.log("Print-customerRewards-API-response: " + res.data);
        this.setState({
          customer_rewards: res.data
        })
      })
      .catch(err => {
        console.log("Error from customerRewards");
      })
  };


  render() {

    const customer = this.state.customer;
    console.log("customerID", customer);
    let CustomerItem = <div>
      {customer.purchase_history}
    </div>

    return (
      <div >
        <div className="container">
          <div className="row">
            <div className="col-md-10 m-auto">
              <br /> <br />
              <Link to="/" className="btn btn-outline-warning float-left">
                  Back
              </Link>
            </div>
            <br />
            <div className="col-md-8 m-auto">
              <h1 className="display-4 text-center">{customer.name}</h1>
              <p className="lead text-center">
                  {customer.account}
              </p>
              <hr /> <br />
            </div>
          </div>
          <div>
            { CustomerItem }
          </div>

        </div>
      </div>
    );
  }
}

export default customerRewards;
