import React, { Component } from 'react';
import RewardsCard from "./RewardsCard";

export default class purchaseMonth extends Component {
  constructor(props) {
    super(props);
      this.state = {
        transitionArr: this.props.transitions,
        monthListSum: [],
        totalRewardsMonth: 0
    };
  }

  calculateRewards = (purchased) => {
    let purchasedT = purchased;
    let totalRewards = 0
    if(50 < purchasedT && purchasedT < 100) {
      let getRewards1 = purchasedT - 50;
      totalRewards += getRewards1;
    }
    if(purchasedT > 100) {
      let getRewards2 = (purchasedT - 100) * 2;
      totalRewards += getRewards2;
    }
    return totalRewards;
  }

  renderRewardTransArr = () => {
    // const total = transitions.reduce((sum, {purchased}) => {
    //   let reward = calculateRewards(purchased);
    //   return sum + reward;
    // }, 0);
    let newtransArr = [...this.state.transitionArr];
    let ntranstArr = newtransArr.map((trans) => {
      return {...trans, rewardpoints: this.calculateRewards(trans.purchased)}
    })
    // this.setState(prevState => ({
    //   transitionArr: [...prevState.transitionArr, newtransArr]
    // }));
    this.setState({
      transitionArr: ntranstArr
    });
    // console.log("new Transition Arr", this.state.transitionArr);
  }

  render() {
    const transitionList = this.state.transitionArr;
    console.log("new Transition Arr", this.state.transitionArr);

    return (
      <div>
        <h2>{this.props.month}</h2>
        {/* <h3>{total}</h3> */}
        <div>
        {transitionList.map((eachTransition) => (
          <RewardsCard
          {...eachTransition}
          key={eachTransition.id}
          />
        ))}
        </div>
      </div>
    )
  }
}
