import React, { Component } from 'react';
import RewardsCard from "./RewardsCard";

export default class purchaseMonth extends Component {
  constructor(props) {
    super(props);
      this.state = {
        transitionArr: this.props.transitions,
        monthListSum: [],
        totalRewardsMonth: 0
    };
  }

  calculateRewards = (purchased) => {
    let purchasedT = this.props.purchased;
    let totalRewards = 0
    if(50 < purchasedT && purchasedT < 100) {
      let getRewards1 = purchasedT - 50;
      totalRewards += getRewards1;
    }
    if(purchasedT > 100) {
      let getRewards2 = (purchasedT - 100) * 2;
      totalRewards += getRewards2;
    }
    return totalRewards;
  }

  TotalMonthRewards = (totalRewardsM) => {
    // // console.log("Month Reward T", totalRewardsM);
    // this.setState(prevState => ({
    //   monthListSum: [...prevState.monthListSum, totalRewardsM]
    // }));
    // // console.log("Month List Arr", totalRewardM);
    // console.log("Month List Arr2", this.state.monthListSum);
    this.setState({
      monthListSum: [...this.state.monthListSum, totalRewardsM]
    }, ()=>{ console.log(this.state.monthListSum)});
  }

  render() {
    const transitionList = this.state.transitionArr;
    return (
      <div>
        <h2>{this.props.month}</h2>
        <h3>{this.TotalMonthRewards}</h3>
        <div>
        {transitionList.map((eachTransition, i) => (
          <RewardsCard
          {...eachTransition}
          key={i}
          purchased={eachTransition.purchase}
          rewardsPoints={this.TotalMonthRewards}
          />
        ))}
        </div>
      </div>
    )
  }
}
